    // Temporarily skip email sending - just log as sent for now
    if (!this.config.smtpPassword) {
      console.log(`ðŸ“§ Email functionality disabled - would have sent onboarding email to ${tenant.adminEmail}`);
      console.log(`ðŸ“§ Tenant "${tenant.name}" created successfully with API keys:`);
      console.log(`ðŸ“§ Auth API Key: ${tenant.authApiKey}`);
      console.log(`ðŸ“§ RBAC API Key: ${tenant.rbacApiKey}`);

      // Log as sent for platform functionality
      await storage.logEmail({
        tenantId: tenant.id,
        recipientEmail: tenant.adminEmail,
        subject,
        templateType: 'onboarding',
        status: 'sent',
        errorMessage: 'Email disabled - credentials not configured'
      });

      return true;
    }

    const html = this.generateOnboardingEmailTemplate(tenant);

    try {
      await this.transporter.sendMail({
        from: `"${this.config.fromName}" <${this.config.fromEmail}>`,
        to: tenant.adminEmail,
        subject,
        html
      });

      // Log successful email
      await storage.logEmail({
        tenantId: tenant.id,
        recipientEmail: tenant.adminEmail,
        subject,
        templateType: 'onboarding',
        status: 'sent',
        errorMessage: null
      });

      return true;
    } catch (error) {
      console.error('Failed to send onboarding email:', error);

      // Log failed email
      await storage.logEmail({
        tenantId: tenant.id,
        recipientEmail: tenant.adminEmail,
        subject,
        templateType: 'onboarding',
        status: 'failed',
        errorMessage: error instanceof Error ? error.message : 'Unknown error'
      });

      return false;
    }
  }

