  id: string;
  orgId: string;
  name: string;
  adminEmail: string;
  status: 'pending' | 'active' | 'suspended';
  authApiKey: string;
  rbacApiKey: string;
  enabledModules: string[];
  moduleConfigs: Record<string, any>;
  createdAt: string;
  updatedAt: string;
}

