  orgId: string;
  name: string;
  adminEmail: string;
  sendEmail?: boolean;
  enabledModules: string[];
  moduleConfigs: Record<string, any>;
}

export const api = {
  // Tenant operations
  async getTenants(): Promise<Tenant[]> {
    const response = await apiRequest('GET', '/api/tenants');
    return response.json();
