      emailProvider: z.string().optional(),
    }).optional(),
    "ai-copilot": z.object({
      provider: z.string().optional(),
      model: z.string().optional(),
    }).optional(),
  }).default({}),
});

type FormData = z.infer<typeof formSchema>;

export default function AddTenantPage() {
  const [, setLocation] = useLocation();
  const createTenant = useCreateTenant();

  const form = useForm<FormData>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      name: "",
      orgId: "",
      adminEmail: "",
      sendEmail: true,
      enabledModules: ["auth", "rbac"],
      moduleConfigs: {},
    },
  });

  const onSubmit = async (data: FormData) => {
    try {
      await createTenant.mutateAsync(data);
      setLocation("/tenants");
    } catch (error) {
      // Error is handled by the mutation
    }
  };

  // Auto-generate org ID from org name
  const handleNameChange = (value: string) => {
    const autoOrgId = value
      .toLowerCase()
      .replace(/[^a-z0-9\s-]/g, '')
      .replace(/\s+/g, '-')
      .replace(/-+/g, '-')
      .replace(/^-|-$/g, '');

    if (!form.getValues("orgId") || form.getValues("orgId") === autoOrgId.slice(0, -1)) {
      form.setValue("orgId", autoOrgId);
    }
  };

  const watchedModules = form.watch("enabledModules");

  return (
    <div className="min-h-screen bg-slate-50 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center gap-4 mb-8">
          <Button
            variant="ghost"
            onClick={() => setLocation("/tenants")}
            className="flex items-center gap-2"
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4" />
            Back to Tenants
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-slate-800">Add New Tenant</h1>
            <p className="text-slate-600">Create a new tenant organization with authentication modules</p>
          </div>
        </div>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-8">
            {/* Basic Information */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Building2 className="h-5 w-5" />
                  Basic Information
                </CardTitle>
                <CardDescription>
                  Enter the organization details and admin contact information
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
                  <FormField
                    control={form.control}
                    name="name"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Organization Name</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="Acme Corporation"
                            {...field}
                            onChange={(e) => {
                              field.onChange(e);
                              handleNameChange(e.target.value);
                            }}
                            data-testid="input-org-name"
                          />
                        </FormControl>
                        <FormDescription>
                          The display name for this organization
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="orgId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Organization ID</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="acme-corp"
                            {...field}
                            data-testid="input-org-id"
                          />
                        </FormControl>
                        <FormDescription>
                          Unique identifier (lowercase, numbers, hyphens only)
                        </FormDescription>
                        <FormMessage />
                      </FormItem>
                    )}
                  />
                </div>

                <FormField
                  control={form.control}
                  name="adminEmail"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Administrator Email</FormLabel>
                      <FormControl>
                        <Input
                          type="email"
                          placeholder="admin@acme.com"
                          {...field}
                          data-testid="input-admin-email"
                        />
                      </FormControl>
                      <FormDescription>
                        Primary contact and initial admin user for this tenant
                      </FormDescription>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                <FormField
                  control={form.control}
                  name="sendEmail"
                  render={({ field }) => (
                    <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                      <FormControl>
                        <Checkbox
                          checked={field.value}
                          onCheckedChange={field.onChange}
                          data-testid="checkbox-send-email"
                        />
                      </FormControl>
                      <div className="space-y-1 leading-none">
                        <FormLabel>Send Welcome Email</FormLabel>
                        <FormDescription>
                          Automatically send onboarding instructions and API keys to the admin
                        </FormDescription>
                      </div>
                    </FormItem>
                  )}
                />
              </CardContent>
            </Card>

            {/* Authentication Modules */}
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  <Settings className="h-5 w-5" />
                  Authentication Modules
                </CardTitle>
                <CardDescription>
                  Select which authentication modules to enable for this tenant
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <FormField
                  control={form.control}
                  name="enabledModules"
                  render={() => (
                    <FormItem>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        {[
                          { id: "auth", label: "Authentication", description: "Basic authentication with email/password", required: true },
                          { id: "rbac", label: "Role-Based Access Control", description: "Permission management system", required: false },
                          { id: "azure-ad", label: "Azure Active Directory", description: "Microsoft Azure AD integration", required: false },
                          { id: "auth0", label: "Auth0", description: "Auth0 identity platform integration", required: false },
                          { id: "saml", label: "SAML", description: "Security Assertion Markup Language integration", required: false },
                          { id: "logging", label: "Logging & Monitoring", description: "Comprehensive audit trail and security monitoring", required: false },
                          { id: "notifications", label: "Notifications", description: "Multi-channel messaging and alerts system", required: false },
                          { id: "ai-copilot", label: "AI Copilot", description: "Intelligent automation and user assistance", required: false },
                        ].map((module) => (
                          <FormField
                            key={module.id}
                            control={form.control}
                            name="enabledModules"
                            render={({ field }) => (
                              <FormItem className="flex flex-row items-start space-x-3 space-y-0 rounded-md border p-4">
                                <FormControl>
                                  <Checkbox
                                    checked={field.value?.includes(module.id as any)}
                                    onCheckedChange={(checked) => {
                                      return checked
                                        ? field.onChange([...field.value, module.id])
                                        : field.onChange(field.value?.filter((value: string) => value !== module.id))
                                    }}
                                    disabled={module.required}
                                    data-testid={`checkbox-module-${module.id}`}
                                  />
                                </FormControl>
                                <div className="space-y-1 leading-none">
                                  <FormLabel className="text-sm font-medium">
                                    {module.label}
                                    {module.required && <span className="text-red-500 ml-1">*</span>}
                                  </FormLabel>
                                  <FormDescription className="text-xs">
                                    {module.description}
                                  </FormDescription>
                                </div>
                              </FormItem>
                            )}
                          />
                        ))}
                      </div>
                      <FormMessage />
                    </FormItem>
                  )}
                />

                {/* Module Configurations */}
                {watchedModules.includes("rbac") && (
                  <div className="space-y-4 border-t pt-4">
                    <h4 className="text-sm font-medium">RBAC Configuration</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <div>
                        <Label className="text-sm font-medium">Permission Template</Label>
                        <Select defaultValue="standard" onValueChange={(value) => {
                          const currentConfigs = form.getValues("moduleConfigs") || {};
                          form.setValue("moduleConfigs", {
                            ...currentConfigs,
                            rbac: { permissionTemplate: value, businessType: 'general' }
                          });
                        }}>
                          <SelectTrigger>
                            <SelectValue placeholder="Select template" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="minimal">Minimal</SelectItem>
                            <SelectItem value="standard">Standard</SelectItem>
                            <SelectItem value="enterprise">Enterprise</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                      <div>
                        <Label className="text-sm font-medium">Business Type</Label>
                        <Select defaultValue="general">
                          <SelectTrigger>
                            <SelectValue placeholder="Select business type" />
                          </SelectTrigger>
                          <SelectContent>
                            <SelectItem value="general">General</SelectItem>
                            <SelectItem value="saas">SaaS</SelectItem>
                            <SelectItem value="ecommerce">E-commerce</SelectItem>
                          </SelectContent>
                        </Select>
                      </div>
                    </div>
                  </div>
                )}

                {watchedModules.includes("azure-ad") && (
                  <div className="space-y-4 border-t pt-4">
                    <h4 className="text-sm font-medium">Azure AD Configuration</h4>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      <FormField
                        control={form.control}
                        name="moduleConfigs.azure-ad.tenantId"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Tenant ID *</FormLabel>
                            <FormControl>
                              <Input placeholder="xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="moduleConfigs.azure-ad.clientId"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Client ID *</FormLabel>
                            <FormControl>
                              <Input placeholder="xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="moduleConfigs.azure-ad.clientSecret"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Client Secret *</FormLabel>
                            <FormControl>
                              <Input type="password" placeholder="Your client secret" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                      <FormField
                        control={form.control}
                        name="moduleConfigs.azure-ad.redirectUri"
                        render={({ field }) => (
                          <FormItem>
                            <FormLabel>Redirect URI</FormLabel>
                            <FormControl>
                              <Input placeholder="https://your-app.com/auth/callback" {...field} />
                            </FormControl>
                            <FormMessage />
                          </FormItem>
                        )}
                      />
                    </div>
                  </div>
                )}

                {watchedModules.includes("auth0") && (
                  <div className="space-y-4 border-t pt-4">
                    <h4 className="text-sm font-medium">Auth0 Configuration</h4>
                    <div className="space-y-4">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="moduleConfigs.auth0.domain"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Domain *</FormLabel>
                              <FormControl>
                                <Input placeholder="your-domain.auth0.com" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="moduleConfigs.auth0.clientId"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Client ID *</FormLabel>
                              <FormControl>
                                <Input placeholder="Your Auth0 Client ID" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="moduleConfigs.auth0.clientSecret"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Client Secret *</FormLabel>
                              <FormControl>
                                <Input type="password" placeholder="Your client secret" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="moduleConfigs.auth0.audience"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Audience</FormLabel>
                              <FormControl>
                                <Input placeholder="https://your-api.auth0.com" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                    </div>
                  </div>
                )}

                {watchedModules.includes("saml") && (
                  <div className="space-y-4 border-t pt-4">
                    <h4 className="text-sm font-medium">SAML Configuration</h4>
                    <div className="space-y-4">
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <FormField
                          control={form.control}
                          name="moduleConfigs.saml.entryPoint"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Entry Point URL *</FormLabel>
                              <FormControl>
                                <Input placeholder="https://your-idp.com/sso" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                        <FormField
                          control={form.control}
                          name="moduleConfigs.saml.issuer"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Issuer *</FormLabel>
                              <FormControl>
                                <Input placeholder="your-saml-issuer" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                        <div>
                          <Label className="text-sm font-medium">Identifier Format</Label>
                          <Select defaultValue="urn:oasis:names:tc:SAML:1.1:nameid-format:emailAddress">
                            <SelectTrigger>
                              <SelectValue placeholder="Select format" />
                            </SelectTrigger>
                            <SelectContent>
                              <SelectItem value="urn:oasis:names:tc:SAML:1.1:nameid-format:emailAddress">Email Address</SelectItem>
                              <SelectItem value="urn:oasis:names:tc:SAML:2.0:nameid-format:persistent">Persistent</SelectItem>
                              <SelectItem value="urn:oasis:names:tc:SAML:2.0:nameid-format:transient">Transient</SelectItem>
                            </SelectContent>
                          </Select>
                        </div>
                        <FormField
                          control={form.control}
                          name="moduleConfigs.saml.callbackUrl"
                          render={({ field }) => (
                            <FormItem>
                              <FormLabel>Callback URL</FormLabel>
                              <FormControl>
                                <Input placeholder="https://your-app.com/saml/acs" {...field} />
                              </FormControl>
                              <FormMessage />
                            </FormItem>
                          )}
                        />
                      </div>
                    </div>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Submit Button */}
            <div className="flex justify-end gap-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => setLocation("/tenants")}
                data-testid="button-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={createTenant.isPending}
                data-testid="button-create-tenant"
              >
                {createTenant.isPending ? "Creating..." : "Create Tenant"}
              </Button>
            </div>
          </form>
        </Form>
      </div>
    </div>
  );
}

client/src/pages/auth-error.tsx.content:
import { useEffect, useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { AlertCircle, ArrowLeft } from "lucide-react";
import { useLocation } from "wouter";

export default function AuthErrorPage() {
  const [, setLocation] = useLocation();
  const [error, setError] = useState<string | null>(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    setError(urlParams.get('error'));
  }, []);

  const getErrorMessage = (error: string | null) => {
    switch (error) {
      case 'callback_failed':
        return 'The authentication callback failed. Please check your configuration and try again.';
      case 'invalid_config':
        return 'Invalid configuration. Please verify your credentials and settings.';
      case 'access_denied':
        return 'Access was denied. Please check your permissions and try again.';
      default:
        return 'An unknown error occurred during authentication.';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-red-50 to-pink-50 p-6 flex items-center justify-center">
      <div className="max-w-2xl mx-auto">
        <Card className="shadow-lg">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-red-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <AlertCircle className="h-8 w-8 text-red-600" />
            </div>
            <CardTitle className="text-2xl text-red-800">Authentication Failed</CardTitle>
            <CardDescription className="text-lg">
              There was a problem with the authentication process
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            <div className="bg-red-50 border border-red-200 rounded-lg p-4">
              <h4 className="font-semibold text-red-800 mb-2">Error Details</h4>
              <p className="text-sm text-red-700">
                {getErrorMessage(error)}
              </p>
            </div>

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <h4 className="font-semibold text-blue-800 mb-2">Troubleshooting Tips</h4>
              <ul className="text-sm text-blue-700 space-y-1">
                <li>â€¢ Verify your Azure AD tenant ID, client ID, and client secret</li>
                <li>â€¢ Check that the redirect URI is correctly configured</li>
                <li>â€¢ Ensure the application has the necessary permissions</li>
                <li>â€¢ Try the authentication flow again</li>
              </ul>
            </div>

            <div className="flex gap-4">
              <Button
                onClick={() => setLocation("/test-azure")}
                className="flex-1 flex items-center gap-2"
              >
                <ArrowLeft className="h-4 w-4" />
                Try Again
              </Button>
              <Button
                variant="outline"
                onClick={() => setLocation("/admin")}
                className="flex-1"
              >
                Back to Dashboard
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

