import { useEffect, useState } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { CheckCircle, Copy, ExternalLink } from "lucide-react";
import { useLocation } from "wouter";

export default function AuthSuccessPage() {
  const [, setLocation] = useLocation();
  const [params, setParams] = useState<any>(null);

  useEffect(() => {
    const urlParams = new URLSearchParams(window.location.search);
    setParams({
      code: urlParams.get('code'),
      provider: urlParams.get('provider')
    });
  }, []);

  const copyCode = () => {
    if (params?.code) {
      navigator.clipboard.writeText(params.code);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-green-50 to-emerald-50 p-6 flex items-center justify-center">
      <div className="max-w-2xl mx-auto">
        <Card className="shadow-lg">
          <CardHeader className="text-center">
            <div className="w-16 h-16 bg-green-100 rounded-full flex items-center justify-center mx-auto mb-4">
              <CheckCircle className="h-8 w-8 text-green-600" />
            </div>
            <CardTitle className="text-2xl text-green-800">Authentication Successful!</CardTitle>
            <CardDescription className="text-lg">
              {params?.provider === 'azure-ad' ? 'Azure AD' : 'OAuth'} authentication completed successfully
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-6">
            {params?.code && (
              <div className="bg-gray-50 border border-gray-200 rounded-lg p-4">
                <div className="flex justify-between items-center mb-2">
                  <h4 className="font-semibold text-gray-800">Authorization Code</h4>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={copyCode}
                    className="flex items-center gap-2"
                  >
                    <Copy className="h-4 w-4" />
                    Copy
                  </Button>
                </div>
                <code className="block text-xs bg-white p-3 rounded border break-all">
                  {params.code}
                </code>
              </div>
            )}

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <h4 className="font-semibold text-blue-800 mb-2">What happens next?</h4>
              <ul className="text-sm text-blue-700 space-y-1">
                <li>â€¢ Your authorization code can be exchanged for an access token</li>
                <li>â€¢ Use this token to authenticate users in your application</li>
                <li>â€¢ The token provides access to user profile information</li>
                <li>â€¢ Integration test completed successfully!</li>
              </ul>
            </div>

            <div className="flex gap-4">
              <Button
                onClick={() => setLocation("/test-azure")}
                className="flex-1"
              >
                Test Again
              </Button>
              <Button
                variant="outline"
                onClick={() => setLocation("/admin")}
                className="flex-1"
              >
                Back to Dashboard
              </Button>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/azure-test.tsx.content:
import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { ArrowLeft, ExternalLink, CheckCircle, AlertCircle } from "lucide-react";
import { useLocation } from "wouter";

const azureConfigSchema = z.object({
  tenantId: z.string().min(1, "Tenant ID is required"),
  clientId: z.string().min(1, "Client ID is required"),
  clientSecret: z.string().min(1, "Client Secret is required"),
  testOrgId: z.string().min(1, "Test Organization ID is required")
});

type AzureConfigForm = z.infer<typeof azureConfigSchema>;

export default function AzureTestPage() {
  const [, setLocation] = useLocation();
  const [testResult, setTestResult] = useState<any>(null);
  const [isLoading, setIsLoading] = useState(false);

  const form = useForm<AzureConfigForm>({
    resolver: zodResolver(azureConfigSchema),
    defaultValues: {
      tenantId: "",
      clientId: "",
      clientSecret: "",
      testOrgId: "test-azure"
    }
  });

  const testAzureIntegration = async (data: AzureConfigForm) => {
    setIsLoading(true);
    setTestResult(null);

    try {
      // First, create or update a test tenant with Azure AD configuration
      const tenantPayload = {
        name: "Azure Test Organization",
        orgId: data.testOrgId,
        adminEmail: "admin@test.com",
        sendEmail: false,
        enabledModules: ["auth", "azure-ad"],
        moduleConfigs: {
          "azure-ad": {
            tenantId: data.tenantId,
            clientId: data.clientId,
            clientSecret: data.clientSecret,
            domain: "test-domain.com"
          }
        }
      };

      const response = await fetch('/api/tenants', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(tenantPayload)
      });

      if (response.ok) {
        // Now test the OAuth flow
        const oauthResponse = await fetch(`/api/oauth/azure-ad/${data.testOrgId}`);
        const oauthData = await oauthResponse.json();

        if (oauthData.authUrl) {
          setTestResult({
            success: true,
            message: "Azure AD configuration successful!",
            authUrl: oauthData.authUrl,
            tenant: await response.json()
          });
        } else {
          setTestResult({
            success: false,
            message: "Failed to generate OAuth URL",
            error: oauthData
          });
        }
      } else {
        const errorData = await response.json();
        if (response.status === 400 && errorData.message?.includes('already exists')) {
          // Tenant already exists, just test OAuth
          const oauthResponse = await fetch(`/api/oauth/azure-ad/${data.testOrgId}`);
          const oauthData = await oauthResponse.json();

          if (oauthData.authUrl) {
            setTestResult({
              success: true,
              message: "Azure AD configuration updated and tested successfully!",
              authUrl: oauthData.authUrl,
              existingTenant: true
            });
          } else {
            setTestResult({
              success: false,
              message: "OAuth URL generation failed",
              error: oauthData
            });
          }
        } else {
          setTestResult({
            success: false,
            message: "Failed to create test tenant",
            error: errorData
          });
        }
      }
    } catch (error) {
      setTestResult({
        success: false,
        message: "Network error occurred",
        error: error instanceof Error ? error.message : 'Unknown error'
      });
    } finally {
      setIsLoading(false);
    }
  };

  const openAzureAuth = () => {
    if (testResult?.authUrl) {
      window.open(testResult.authUrl, '_blank');
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-50 p-6">
      <div className="max-w-4xl mx-auto">
        {/* Header */}
        <div className="flex items-center gap-4 mb-8">
          <Button
            variant="ghost"
            onClick={() => setLocation("/admin")}
            className="flex items-center gap-2"
            data-testid="button-back"
          >
            <ArrowLeft className="h-4 w-4" />
            Back to Dashboard
          </Button>
          <div>
            <h1 className="text-3xl font-bold text-slate-800">Azure AD Integration Test</h1>
            <p className="text-slate-600">Test your Azure AD configuration with real credentials</p>
          </div>
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
          {/* Configuration Form */}
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center gap-2">
                <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center">
                  <svg className="w-5 h-5 text-white" viewBox="0 0 24 24" fill="currentColor">
                    <path d="M12 2L2 7v10c0 5.55 3.84 9.74 9 11 5.16-1.26 9-5.45 9-11V7l-10-5z"/>
                  </svg>
                </div>
                Azure AD Configuration
              </CardTitle>
              <CardDescription>
                Enter your Azure AD application credentials to test the integration
              </CardDescription>
            </CardHeader>
            <CardContent>
              <Form {...form}>
                <form onSubmit={form.handleSubmit(testAzureIntegration)} className="space-y-4">
                  <FormField
                    control={form.control}
                    name="tenantId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Azure Tenant ID</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx"
                            {...field}
                            data-testid="input-tenant-id"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="clientId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Application (Client) ID</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxxxxxx"
                            {...field}
                            data-testid="input-client-id"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="clientSecret"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Client Secret</FormLabel>
                        <FormControl>
                          <Input
                            type="password"
                            placeholder="Enter your client secret"
                            {...field}
                            data-testid="input-client-secret"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <FormField
                    control={form.control}
                    name="testOrgId"
                    render={({ field }) => (
                      <FormItem>
                        <FormLabel>Test Organization ID</FormLabel>
                        <FormControl>
                          <Input
                            placeholder="test-azure"
                            {...field}
                            data-testid="input-org-id"
                          />
                        </FormControl>
                        <FormMessage />
                      </FormItem>
                    )}
                  />

                  <Button
                    type="submit"
                    disabled={isLoading}
                    className="w-full"
                    data-testid="button-test-azure"
                  >
                    {isLoading ? "Testing..." : "Test Azure AD Integration"}
                  </Button>
                </form>
              </Form>
            </CardContent>
          </Card>

          {/* Results */}
          <Card>
            <CardHeader>
              <CardTitle>Test Results</CardTitle>
              <CardDescription>
                Integration test results and authentication URL
              </CardDescription>
            </CardHeader>
            <CardContent>
              {!testResult ? (
                <div className="text-center py-8 text-slate-500">
                  <AlertCircle className="h-16 w-16 mx-auto mb-4 text-slate-300" />
                  <p>Enter your Azure AD credentials and click "Test Integration" to see results</p>
                </div>
              ) : (
                <div className="space-y-4">
                  <div className={`flex items-center gap-3 p-4 rounded-lg ${
                    testResult.success 
                      ? 'bg-green-50 border border-green-200' 
                      : 'bg-red-50 border border-red-200'
                  }`}>
                    {testResult.success ? (
                      <CheckCircle className="h-5 w-5 text-green-500" />
                    ) : (
                      <AlertCircle className="h-5 w-5 text-red-500" />
                    )}
                    <span className={testResult.success ? 'text-green-700' : 'text-red-700'}>
                      {testResult.message}
                    </span>
                  </div>

                  {testResult.success && testResult.authUrl && (
                    <div className="space-y-3">
                      <div className="p-4 bg-blue-50 border border-blue-200 rounded-lg">
                        <h4 className="font-semibold text-blue-800 mb-2">Authentication URL Generated</h4>
                        <p className="text-sm text-blue-700 mb-3">
                          Click the button below to test the OAuth flow in a new window
                        </p>
                        <Button
                          onClick={openAzureAuth}
                          className="w-full flex items-center gap-2"
                          data-testid="button-open-auth"
                        >
                          <ExternalLink className="h-4 w-4" />
                          Open Azure AD Login
                        </Button>
                      </div>

                      <div className="text-sm text-slate-600 space-y-1">
                        <p><strong>Test Organization:</strong> {form.getValues("testOrgId")}</p>
                        <p><strong>Tenant ID:</strong> {form.getValues("tenantId")}</p>
                        <p><strong>Status:</strong> Ready for authentication</p>
                      </div>
                    </div>
                  )}

                  {!testResult.success && testResult.error && (
                    <div className="bg-red-50 border border-red-200 rounded-lg p-4">
                      <h4 className="font-semibold text-red-800 mb-2">Error Details</h4>
                      <pre className="text-xs text-red-700 overflow-auto">
                        {JSON.stringify(testResult.error, null, 2)}
                      </pre>
                    </div>
                  )}
                </div>
              )}
            </CardContent>
          </Card>
        </div>

        {/* Instructions */}
        <Card className="mt-8">
          <CardHeader>
            <CardTitle>Setup Instructions</CardTitle>
            <CardDescription>
              How to get your Azure AD credentials for testing
            </CardDescription>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div className="space-y-3">
                <h4 className="font-semibold">1. Register Application in Azure</h4>
                <ul className="text-sm text-slate-600 space-y-1">
                  <li>â€¢ Go to Azure Portal â†’ App Registrations</li>
                  <li>â€¢ Click "New registration"</li>
                  <li>â€¢ Set redirect URI to your callback URL</li>
                  <li>â€¢ Note the Application (client) ID</li>
                </ul>
              </div>
              <div className="space-y-3">
                <h4 className="font-semibold">2. Create Client Secret</h4>
                <ul className="text-sm text-slate-600 space-y-1">
                  <li>â€¢ Go to Certificates & secrets</li>
                  <li>â€¢ Click "New client secret"</li>
                  <li>â€¢ Copy the secret value immediately</li>
                  <li>â€¢ Find your Tenant ID in the Overview tab</li>
                </ul>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/logs.tsx.content:
import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { RefreshCw, Filter, Download, Clock, Mail, Settings, User } from "lucide-react";
import { format } from "date-fns";

interface SystemLog {
  id: string;
  tenantId: string | null;
  adminUserId: string | null;
  action: string;
  entityType: string;
  entityId: string;
  details: any;
  ipAddress: string | null;
  userAgent: string | null;
  timestamp: string;
  tenantName: string | null;
}

interface EmailLog {
  id: string;
  tenantId: string | null;
  recipientEmail: string;
  subject: string;
  templateType: string;
  status: string;
  sentAt: string;
  errorMessage: string | null;
  tenantName: string | null;
}

export default function LogsPage() {
  const [systemFilters, setSystemFilters] = useState({
    tenantId: "",
    action: "",
    limit: 50,
    offset: 0
  });

  const [emailFilters, setEmailFilters] = useState({
    tenantId: "",
    status: "",
    limit: 50,
    offset: 0
  });

  // System logs query
  const systemLogsQuery = useQuery({
    queryKey: ["/api/logs/system", systemFilters],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (systemFilters.tenantId) params.append("tenantId", systemFilters.tenantId);
      if (systemFilters.action) params.append("action", systemFilters.action);
      params.append("limit", systemFilters.limit.toString());
      params.append("offset", systemFilters.offset.toString());

      const response = await fetch(`/api/logs/system?${params}`);
      if (!response.ok) throw new Error("Failed to fetch system logs");
      return response.json() as Promise<SystemLog[]>;
    }
  });

  // Email logs query
  const emailLogsQuery = useQuery({
    queryKey: ["/api/logs/email", emailFilters],
    queryFn: async () => {
      const params = new URLSearchParams();
      if (emailFilters.tenantId) params.append("tenantId", emailFilters.tenantId);
      if (emailFilters.status) params.append("status", emailFilters.status);
      params.append("limit", emailFilters.limit.toString());
      params.append("offset", emailFilters.offset.toString());

      const response = await fetch(`/api/logs/email?${params}`);
      if (!response.ok) throw new Error("Failed to fetch email logs");
      return response.json() as Promise<EmailLog[]>;
    }
  });

  const getActionIcon = (action: string) => {
    switch (action) {
      case 'modules_updated':
        return <Settings className="h-4 w-4" />;
      case 'tenant_created':
        return <User className="h-4 w-4" />;
      case 'tenant_status_updated':
        return <Settings className="h-4 w-4" />;
      default:
        return <Clock className="h-4 w-4" />;
    }
  };

  const getActionColor = (action: string) => {
    switch (action) {
      case 'modules_updated':
        return 'bg-blue-100 text-blue-800';
      case 'tenant_created':
        return 'bg-green-100 text-green-800';
      case 'tenant_status_updated':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'sent':
        return 'bg-green-100 text-green-800';
      case 'failed':
        return 'bg-red-100 text-red-800';
      case 'pending':
        return 'bg-yellow-100 text-yellow-800';
      default:
        return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold text-slate-800">System Logs</h1>
        <p className="text-slate-600 mt-1">Monitor system activities and email delivery status</p>
      </div>

      <Tabs defaultValue="system" className="space-y-6">
        <TabsList className="grid w-full grid-cols-2">
          <TabsTrigger value="system" data-testid="tab-system-logs">
            <Settings className="h-4 w-4 mr-2" />
            System Logs
          </TabsTrigger>
          <TabsTrigger value="email" data-testid="tab-email-logs">
            <Mail className="h-4 w-4 mr-2" />
            Email Logs
          </TabsTrigger>
        </TabsList>

        <TabsContent value="system" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center gap-2">
                  <Settings className="h-5 w-5" />
                  System Activity Logs
                </CardTitle>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => systemLogsQuery.refetch()}
                    disabled={systemLogsQuery.isLoading}
                    data-testid="button-refresh-system"
                  >
                    <RefreshCw className={`h-4 w-4 mr-2 ${systemLogsQuery.isLoading ? 'animate-spin' : ''}`} />
                    Refresh
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Filters */}
              <div className="flex gap-4 items-center p-4 bg-slate-50 rounded-lg">
                <Filter className="h-4 w-4 text-slate-500" />
                <Input
                  placeholder="Filter by tenant ID..."
                  value={systemFilters.tenantId}
                  onChange={(e) => setSystemFilters(prev => ({ ...prev, tenantId: e.target.value }))}
                  className="max-w-xs"
                  data-testid="input-system-tenant-filter"
                />
                <Select value={systemFilters.action} onValueChange={(value) => setSystemFilters(prev => ({ ...prev, action: value }))}>
                  <SelectTrigger className="max-w-xs" data-testid="select-system-action-filter">
                    <SelectValue placeholder="Filter by action..." />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All actions</SelectItem>
                    <SelectItem value="modules_updated">Module Updates</SelectItem>
                    <SelectItem value="tenant_created">Tenant Created</SelectItem>
                    <SelectItem value="tenant_status_updated">Status Updates</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* System Logs List */}
              <div className="space-y-3">
                {systemLogsQuery.isLoading ? (
                  <div className="text-center py-8 text-slate-500">Loading system logs...</div>
                ) : systemLogsQuery.data?.length === 0 ? (
                  <div className="text-center py-8 text-slate-500">No system logs found</div>
                ) : (
                  systemLogsQuery.data?.map((log) => (
                    <Card key={log.id} className="border-l-4 border-l-blue-400" data-testid={`system-log-${log.id}`}>
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex items-start gap-3">
                            <div className={`p-2 rounded-lg ${getActionColor(log.action)}`}>
                              {getActionIcon(log.action)}
                            </div>
                            <div className="space-y-1">
                              <div className="flex items-center gap-2">
                                <Badge variant="outline" className="text-xs">
                                  {log.action.replace('_', ' ').toUpperCase()}
                                </Badge>
                                <Badge variant="secondary" className="text-xs">
                                  {log.entityType}
                                </Badge>
                              </div>
                              <p className="text-sm text-slate-800">
                                <span className="font-medium">{log.tenantName || 'System'}</span>
                                {log.details && Object.keys(log.details).length > 0 && (
                                  <span className="text-slate-600 ml-2">
                                    {JSON.stringify(log.details, null, 0)}
                                  </span>
                                )}
                              </p>
                              <div className="flex items-center gap-2 text-xs text-slate-500">
                                <Clock className="h-3 w-3" />
                                {format(new Date(log.timestamp), 'MMM d, yyyy h:mm a')}
                                {log.ipAddress && (
                                  <span className="ml-2">IP: {log.ipAddress}</span>
                                )}
                              </div>
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>

        <TabsContent value="email" className="space-y-6">
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="flex items-center gap-2">
                  <Mail className="h-5 w-5" />
                  Email Delivery Logs
                </CardTitle>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => emailLogsQuery.refetch()}
                    disabled={emailLogsQuery.isLoading}
                    data-testid="button-refresh-email"
                  >
                    <RefreshCw className={`h-4 w-4 mr-2 ${emailLogsQuery.isLoading ? 'animate-spin' : ''}`} />
                    Refresh
                  </Button>
                </div>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Filters */}
              <div className="flex gap-4 items-center p-4 bg-slate-50 rounded-lg">
                <Filter className="h-4 w-4 text-slate-500" />
                <Input
                  placeholder="Filter by tenant ID..."
                  value={emailFilters.tenantId}
                  onChange={(e) => setEmailFilters(prev => ({ ...prev, tenantId: e.target.value }))}
                  className="max-w-xs"
                  data-testid="input-email-tenant-filter"
                />
                <Select value={emailFilters.status} onValueChange={(value) => setEmailFilters(prev => ({ ...prev, status: value }))}>
                  <SelectTrigger className="max-w-xs" data-testid="select-email-status-filter">
                    <SelectValue placeholder="Filter by status..." />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All statuses</SelectItem>
                    <SelectItem value="sent">Sent</SelectItem>
                    <SelectItem value="failed">Failed</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              {/* Email Logs List */}
              <div className="space-y-3">
                {emailLogsQuery.isLoading ? (
                  <div className="text-center py-8 text-slate-500">Loading email logs...</div>
                ) : emailLogsQuery.data?.length === 0 ? (
                  <div className="text-center py-8 text-slate-500">No email logs found</div>
                ) : (
                  emailLogsQuery.data?.map((log) => (
                    <Card key={log.id} className="border-l-4 border-l-indigo-400" data-testid={`email-log-${log.id}`}>
                      <CardContent className="p-4">
                        <div className="flex items-start justify-between">
                          <div className="flex items-start gap-3">
                            <div className="p-2 rounded-lg bg-indigo-100 text-indigo-600">
                              <Mail className="h-4 w-4" />
                            </div>
                            <div className="space-y-1">
                              <div className="flex items-center gap-2">
                                <Badge className={getStatusColor(log.status)}>
                                  {log.status.toUpperCase()}
                                </Badge>
                                <Badge variant="secondary" className="text-xs">
                                  {log.templateType}
                                </Badge>
                              </div>
                              <p className="text-sm text-slate-800">
                                <span className="font-medium">{log.subject}</span>
                              </p>
                              <p className="text-sm text-slate-600">
                                To: {log.recipientEmail}
                                {log.tenantName && ` (${log.tenantName})`}
                              </p>
                              <div className="flex items-center gap-2 text-xs text-slate-500">
                                <Clock className="h-3 w-3" />
                                {format(new Date(log.sentAt), 'MMM d, yyyy h:mm a')}
                              </div>
                              {log.errorMessage && (
                                <p className="text-xs text-red-600 bg-red-50 p-2 rounded">
                                  Error: {log.errorMessage}
                                </p>
                              )}
                            </div>
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))
                )}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

client/src/pages/module-management.tsx.content:
import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { useToast } from "@/hooks/use-toast";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { 
  Settings, 
  Shield, 
  Key, 
  Cloud, 
  Users, 
  CheckCircle, 
  XCircle, 
  Edit,
  Save,
  Search,
  FileText,
  Bell,
  Bot
} from "lucide-react";

interface Tenant {
  id: string;
  name: string;
  orgId: string;
  adminEmail: string;
  status: string;
  enabledModules: string[];
  moduleConfigs: Record<string, any>;
}

const moduleUpdateSchema = z.object({
  enabledModules: z.array(z.string()),
  moduleConfigs: z.record(z.any()).optional()
});

type ModuleUpdateFormData = z.infer<typeof moduleUpdateSchema>;

const availableModules = [
  {
    id: 'auth',
    name: 'Core Authentication',
    description: 'JWT-based authentication with user management',
    icon: Key,
    category: 'Authentication',
    required: true
  },
  {
    id: 'rbac',
    name: 'Role-Based Access Control',
    description: 'Advanced role and permission management system',
    icon: Shield,
    category: 'Authorization',
    required: true
  },
  {
    id: 'azure-ad',
    name: 'Azure Active Directory',
    description: 'Single sign-on with Microsoft Azure AD',
    icon: Cloud,
    category: 'SSO',
    configurable: true
  },
  {
    id: 'auth0',
    name: 'Auth0 Integration',
    description: 'Universal authentication with Auth0 platform',
    icon: Users,
    category: 'SSO',
    configurable: true
  },
  {
    id: 'saml',
    name: 'SAML SSO',
    description: 'SAML-based single sign-on integration',
    icon: Settings,
    category: 'SSO',
    configurable: true
  },
  {
    id: 'logging',
    name: 'Logging & Monitoring',
    description: 'Comprehensive audit trail and security monitoring',
    icon: FileText,
    category: 'Operations',
    configurable: true,
    priority: 'high'
  },
  {
    id: 'notifications',
    name: 'Notifications',
    description: 'Multi-channel messaging and alerts system',
    icon: Bell,
    category: 'Communication',
    configurable: true,
    priority: 'medium'
  },
  {
    id: 'ai-copilot',
    name: 'AI Copilot',
    description: 'Intelligent automation and user assistance',
    icon: Bot,
    category: 'AI/ML',
    configurable: true,
    priority: 'low'
  }
];

export default function ModuleManagementPage() {
  const [searchTenant, setSearchTenant] = useState("");
  const [selectedTenant, setSelectedTenant] = useState<Tenant | null>(null);
  const [editingModules, setEditingModules] = useState(false);
  const { toast } = useToast();

  const form = useForm<ModuleUpdateFormData>({
    resolver: zodResolver(moduleUpdateSchema),
    defaultValues: {
      enabledModules: ['auth', 'rbac'],
      moduleConfigs: {}
    }
  });

  // Get all tenants
  const tenantsQuery = useQuery({
    queryKey: ["/api/tenants"],
    queryFn: async () => {
      const response = await fetch("/api/tenants");
      if (!response.ok) throw new Error("Failed to fetch tenants");
      return response.json() as Promise<Tenant[]>;
    }
  });

  // Update tenant modules mutation
  const updateModulesMutation = useMutation({
    mutationFn: async (data: { tenantId: string; modules: ModuleUpdateFormData }) => {
      const response = await fetch(`/api/tenants/${data.tenantId}/modules`, {
        method: "PATCH",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify(data.modules)
      });
      if (!response.ok) throw new Error("Failed to update modules");
      return response.json();
    },
    onSuccess: (_, variables) => {
      queryClient.invalidateQueries({ queryKey: ["/api/tenants"] });
      toast({
        title: "Modules Updated",
        description: "Tenant modules have been successfully updated.",
      });
      setEditingModules(false);
      // Update selected tenant
      if (selectedTenant?.id === variables.tenantId) {
        const updatedTenant = tenantsQuery.data?.find(t => t.id === variables.tenantId);
        if (updatedTenant) {
          setSelectedTenant(updatedTenant);
        }
      }
    },
    onError: (error) => {
      toast({
        title: "Update Failed",
        description: error instanceof Error ? error.message : "Failed to update modules",
        variant: "destructive"
      });
    }
  });

  const filteredTenants = tenantsQuery.data?.filter(tenant =>
    tenant.name.toLowerCase().includes(searchTenant.toLowerCase()) ||
    tenant.orgId.toLowerCase().includes(searchTenant.toLowerCase()) ||
    tenant.adminEmail.toLowerCase().includes(searchTenant.toLowerCase())
  ) || [];

  const handleTenantSelect = (tenant: Tenant) => {
    setSelectedTenant(tenant);
    form.reset({
      enabledModules: tenant.enabledModules || ['auth', 'rbac'],
      moduleConfigs: tenant.moduleConfigs || {}
    });
    setEditingModules(false);
  };

  const onSubmit = (data: ModuleUpdateFormData) => {
    if (!selectedTenant) return;

    updateModulesMutation.mutate({
      tenantId: selectedTenant.id,
      modules: data
    });
  };

  const getModuleStatus = (moduleId: string) => {
    if (!selectedTenant) return false;
    return selectedTenant.enabledModules?.includes(moduleId) || false;
  };

  const getModuleIcon = (moduleId: string) => {
    const module = availableModules.find(m => m.id === moduleId);
    return module?.icon || Settings;
  };

  return (
    <div className="space-y-6">
      <div>
        <h1 className="text-2xl font-semibold text-slate-800">Module Management</h1>
        <p className="text-slate-600 mt-1">Configure authentication and authorization modules for tenants</p>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Tenant Selection */}
        <Card className="lg:col-span-1">
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Users className="h-5 w-5" />
              Select Tenant
            </CardTitle>
          </CardHeader>
          <CardContent className="space-y-4">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-slate-400" />
              <Input
                placeholder="Search tenants..."
                value={searchTenant}
                onChange={(e) => setSearchTenant(e.target.value)}
                className="pl-10"
                data-testid="input-search-tenants"
              />
            </div>

            <div className="space-y-2 max-h-96 overflow-y-auto">
              {tenantsQuery.isLoading ? (
                <div className="text-center py-8 text-slate-500">Loading tenants...</div>
              ) : filteredTenants.length === 0 ? (
                <div className="text-center py-8 text-slate-500">
                  {searchTenant ? 'No tenants found' : 'No tenants available'}
                </div>
              ) : (
                filteredTenants.map((tenant) => (
                  <Card
                    key={tenant.id}
                    className={`cursor-pointer transition-colors hover:bg-slate-50 ${
                      selectedTenant?.id === tenant.id ? 'ring-2 ring-blue-500' : ''
                    }`}
                    onClick={() => handleTenantSelect(tenant)}
                    data-testid={`tenant-card-${tenant.id}`}
                  >
                    <CardContent className="p-4">
                      <div className="space-y-2">
                        <div className="flex items-center justify-between">
                          <h3 className="font-medium text-slate-800">{tenant.name}</h3>
                          <Badge variant={tenant.status === 'active' ? 'default' : 'secondary'}>
                            {tenant.status}
                          </Badge>
                        </div>
                        <p className="text-sm text-slate-600">{tenant.orgId}</p>
                        <div className="flex flex-wrap gap-1 mt-2">
                          {(tenant.enabledModules || ['auth', 'rbac']).map((module) => (
                            <Badge key={module} variant="outline" className="text-xs">
                              {module}
                            </Badge>
                          ))}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                ))
              )}
            </div>
          </CardContent>
        </Card>

        {/* Module Configuration */}
        <div className="lg:col-span-2 space-y-6">
          {selectedTenant ? (
            <>
              <Card>
                <CardHeader>
                  <div className="flex items-center justify-between">
                    <CardTitle className="flex items-center gap-2">
                      <Settings className="h-5 w-5" />
                      Modules for {selectedTenant.name}
                    </CardTitle>
                    <div className="flex gap-2">
                      {!editingModules ? (
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={() => setEditingModules(true)}
                          data-testid="button-edit-modules"
                        >
                          <Edit className="h-4 w-4 mr-2" />
                          Edit Modules
                        </Button>
                      ) : (
                        <div className="flex gap-2">
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => {
                              setEditingModules(false);
                              form.reset({
                                enabledModules: selectedTenant.enabledModules || ['auth', 'rbac'],
                                moduleConfigs: selectedTenant.moduleConfigs || {}
                              });
                            }}
                            data-testid="button-cancel-edit"
                          >
                            Cancel
                          </Button>
                          <Button
                            size="sm"
                            onClick={form.handleSubmit(onSubmit)}
                            disabled={updateModulesMutation.isPending}
                            data-testid="button-save-modules"
                          >
                            <Save className="h-4 w-4 mr-2" />
                            {updateModulesMutation.isPending ? 'Saving...' : 'Save Changes'}
                          </Button>
                        </div>
                      )}
                    </div>
                  </div>
                </CardHeader>
                <CardContent>
                  <Form {...form}>
                    <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
                      <div className="grid gap-4">
                        {availableModules.map((module) => {
                          const Icon = module.icon;
                          const isEnabled = editingModules 
                            ? form.watch('enabledModules')?.includes(module.id)
                            : getModuleStatus(module.id);

                          return (
                            <Card key={module.id} className="border-2" data-testid={`module-card-${module.id}`}>
                              <CardContent className="p-4">
                                <div className="flex items-start justify-between">
                                  <div className="flex items-start gap-3">
                                    <div className={`p-2 rounded-lg ${isEnabled ? 'bg-green-100 text-green-600' : 'bg-slate-100 text-slate-400'}`}>
                                      <Icon className="h-5 w-5" />
                                    </div>
                                    <div className="space-y-1">
                                      <div className="flex items-center gap-2">
                                        <h3 className="font-medium">{module.name}</h3>
                                        <Badge variant="secondary" className="text-xs">
                                          {module.category}
                                        </Badge>
                                        {module.required && (
                                          <Badge variant="outline" className="text-xs">
                                            Required
                                          </Badge>
                                        )}
                                      </div>
                                      <p className="text-sm text-slate-600">{module.description}</p>

                                      {isEnabled && module.configurable && selectedTenant.moduleConfigs?.[module.id] && (
                                        <div className="mt-2 p-2 bg-slate-50 rounded text-xs text-slate-600">
                                          <strong>Configuration:</strong>
                                          <pre className="mt-1">
                                            {JSON.stringify(selectedTenant.moduleConfigs[module.id], null, 2)}
                                          </pre>
                                        </div>
                                      )}
                                    </div>
                                  </div>

                                  <div className="flex items-center gap-2">
                                    {isEnabled ? (
                                      <CheckCircle className="h-5 w-5 text-green-500" />
                                    ) : (
                                      <XCircle className="h-5 w-5 text-slate-400" />
                                    )}

                                    {editingModules && (
                                      <FormField
                                        control={form.control}
                                        name="enabledModules"
                                        render={({ field }) => (
                                          <FormItem>
                                            <FormControl>
                                              <Switch
                                                checked={field.value?.includes(module.id)}
                                                onCheckedChange={(checked) => {
                                                  if (module.required && !checked) return;

                                                  const current = field.value || [];
                                                  if (checked) {
                                                    field.onChange([...current, module.id]);
                                                  } else {
                                                    field.onChange(current.filter(id => id !== module.id));
                                                  }
                                                }}
                                                disabled={module.required}
                                                data-testid={`switch-${module.id}`}
                                              />
                                            </FormControl>
                                          </FormItem>
                                        )}
                                      />
                                    )}
                                  </div>
                                </div>
                              </CardContent>
                            </Card>
                          );
                        })}
                      </div>
                    </form>
                  </Form>
                </CardContent>
              </Card>

              {/* Module Statistics */}
              <Card>
                <CardHeader>
                  <CardTitle>Module Statistics</CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                    <div className="text-center p-4 bg-green-50 rounded-lg">
                      <div className="text-2xl font-bold text-green-700">
                        {selectedTenant.enabledModules?.length || 0}
                      </div>
                      <div className="text-sm text-green-600">Enabled Modules</div>
                    </div>
                    <div className="text-center p-4 bg-blue-50 rounded-lg">
                      <div className="text-2xl font-bold text-blue-700">
                        {availableModules.length}
                      </div>
                      <div className="text-sm text-blue-600">Available Modules</div>
                    </div>
                    <div className="text-center p-4 bg-purple-50 rounded-lg">
                      <div className="text-2xl font-bold text-purple-700">
                        {selectedTenant.enabledModules?.filter(m => 
                          availableModules.find(am => am.id === m)?.category === 'SSO'
                        ).length || 0}
                      </div>
                      <div className="text-sm text-purple-600">SSO Modules</div>
                    </div>
                    <div className="text-center p-4 bg-amber-50 rounded-lg">
                      <div className="text-2xl font-bold text-amber-700">
                        {Object.keys(selectedTenant.moduleConfigs || {}).length}
                      </div>
                      <div className="text-sm text-amber-600">Configured</div>
                    </div>
                  </div>
                </CardContent>
              </Card>
            </>
          ) : (
            <Card className="h-96 flex items-center justify-center">
              <div className="text-center text-slate-500">
                <Settings className="h-12 w-12 mx-auto mb-4 text-slate-300" />
                <h3 className="text-lg font-medium mb-2">No Tenant Selected</h3>
                <p>Select a tenant from the list to manage their modules</p>
              </div>
            </Card>
          )}
        </div>
      </div>
    </div>
  );
}

