import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormLabel,
  FormMessage,
} from "@/components/ui/form";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { useUpdateTenantStatus } from "@/hooks/use-tenants";
import type { Tenant } from "@/lib/api";

const editFormSchema = z.object({
  name: z.string().min(2, "Organization name must be at least 2 characters"),
  adminEmail: z.string().email("Please enter a valid email address"),
  status: z.enum(["pending", "active", "suspended"]),
});

type EditFormData = z.infer<typeof editFormSchema>;

interface EditTenantModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  tenant: Tenant | null;
}

export default function EditTenantModal({ open, onOpenChange, tenant }: EditTenantModalProps) {
  const updateTenantStatus = useUpdateTenantStatus();

  const form = useForm<EditFormData>({
    resolver: zodResolver(editFormSchema),
    defaultValues: {
      name: tenant?.name || "",
      adminEmail: tenant?.adminEmail || "",
      status: tenant?.status || "pending",
    },
  });

  const onSubmit = async (data: EditFormData) => {
    if (!tenant) return;

    try {
      // For now, we only support status updates
      if (data.status !== tenant.status) {
        await updateTenantStatus.mutateAsync({ id: tenant.id, status: data.status });
      }
      onOpenChange(false);
    } catch (error) {
      // Error is handled by the mutation
    }
  };

  if (!tenant) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="sm:max-w-[425px]" data-testid="edit-tenant-modal">
        <DialogHeader>
          <DialogTitle>Edit Tenant</DialogTitle>
        </DialogHeader>

        <Form {...form}>
          <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-4">
            <FormField
              control={form.control}
              name="name"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Organization Name</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      disabled
                      className="bg-slate-100"
                      data-testid="input-edit-name"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div>
              <FormLabel>Organization ID</FormLabel>
              <Input
                value={tenant.orgId}
                disabled
                className="bg-slate-100"
                data-testid="input-edit-org-id"
              />
            </div>

            <FormField
              control={form.control}
              name="adminEmail"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Admin Email</FormLabel>
                  <FormControl>
                    <Input
                      {...field}
                      disabled
                      className="bg-slate-100"
                      data-testid="input-edit-email"
                    />
                  </FormControl>
                  <FormMessage />
                </FormItem>
              )}
            />

            <FormField
              control={form.control}
              name="status"
              render={({ field }) => (
                <FormItem>
                  <FormLabel>Status</FormLabel>
                  <Select onValueChange={field.onChange} defaultValue={field.value}>
                    <FormControl>
                      <SelectTrigger data-testid="select-edit-status">
                        <SelectValue placeholder="Select status" />
                      </SelectTrigger>
                    </FormControl>
                    <SelectContent>
                      <SelectItem value="pending">Pending</SelectItem>
                      <SelectItem value="active">Active</SelectItem>
                      <SelectItem value="suspended">Suspended</SelectItem>
                    </SelectContent>
                  </Select>
                  <FormMessage />
                </FormItem>
              )}
            />

            <div className="flex items-center justify-end space-x-3 pt-4">
              <Button
                type="button"
                variant="outline"
                onClick={() => onOpenChange(false)}
                data-testid="button-edit-cancel"
              >
                Cancel
              </Button>
              <Button
                type="submit"
                disabled={updateTenantStatus.isPending}
                data-testid="button-edit-save"
              >
                {updateTenantStatus.isPending ? "Saving..." : "Save Changes"}
              </Button>
            </div>
          </form>
        </Form>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/modals/view-tenant.content:
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "@/components/ui/dialog";
import { Badge } from "@/components/ui/badge";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Copy, Eye, EyeOff } from "lucide-react";
import { useState } from "react";
import { useToast } from "@/hooks/use-toast";
import type { Tenant } from "@/lib/api";

interface ViewTenantModalProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  tenant: Tenant | null;
}

export default function ViewTenantModal({ open, onOpenChange, tenant }: ViewTenantModalProps) {
  const [showApiKeys, setShowApiKeys] = useState(false);
  const { toast } = useToast();

  const copyToClipboard = async (text: string, label: string) => {
    try {
      await navigator.clipboard.writeText(text);
      toast({
        title: "Copied!",
        description: `${label} copied to clipboard`,
      });
    } catch (err) {
      toast({
        title: "Failed to copy",
        description: "Please copy manually",
        variant: "destructive",
      });
    }
  };

  if (!tenant) return null;

  return (
    <Dialog open={open} onOpenChange={onOpenChange}>
      <DialogContent className="max-w-2xl" data-testid="view-tenant-modal">
        <DialogHeader>
          <DialogTitle>Tenant Details</DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Basic Info */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Basic Information</CardTitle>
            </CardHeader>
            <CardContent className="grid grid-cols-2 gap-4">
              <div>
                <label className="text-sm font-medium text-slate-700">Organization Name</label>
                <p className="text-slate-900 font-medium">{tenant.name}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">Organization ID</label>
                <p className="text-slate-900 font-mono">{tenant.orgId}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">Admin Email</label>
                <p className="text-slate-900">{tenant.adminEmail}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">Status</label>
                <Badge 
                  variant={tenant.status === 'active' ? 'default' : tenant.status === 'pending' ? 'secondary' : 'destructive'}
                  data-testid={`tenant-status-${tenant.status}`}
                >
                  {tenant.status.charAt(0).toUpperCase() + tenant.status.slice(1)}
                </Badge>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">Created</label>
                <p className="text-slate-900">{new Date(tenant.createdAt).toLocaleDateString()}</p>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">Portal URL</label>
                <p className="text-slate-900 font-mono text-sm">
                  /tenant/{tenant.orgId}/login
                </p>
              </div>
            </CardContent>
          </Card>

          {/* API Keys */}
          <Card>
            <CardHeader>
              <div className="flex items-center justify-between">
                <CardTitle className="text-lg">API Keys</CardTitle>
                <Button
                  variant="ghost"
                  onClick={() => setShowApiKeys(!showApiKeys)}
                  data-testid="button-toggle-keys"
                >
                  {showApiKeys ? <EyeOff className="h-4 w-4" /> : <Eye className="h-4 w-4" />}
                  {showApiKeys ? "Hide" : "Show"}
                </Button>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <label className="text-sm font-medium text-slate-700">Authentication API Key</label>
                <div className="flex items-center space-x-2 mt-1">
                  <code className="flex-1 bg-slate-100 px-3 py-2 rounded text-sm font-mono">
                    {showApiKeys ? tenant.authApiKey : 'â€¢'.repeat(32)}
                  </code>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => copyToClipboard(tenant.authApiKey, 'Auth API Key')}
                    data-testid="button-copy-auth"
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                </div>
              </div>
              <div>
                <label className="text-sm font-medium text-slate-700">RBAC API Key</label>
                <div className="flex items-center space-x-2 mt-1">
                  <code className="flex-1 bg-slate-100 px-3 py-2 rounded text-sm font-mono">
                    {showApiKeys ? tenant.rbacApiKey : 'â€¢'.repeat(32)}
                  </code>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={() => copyToClipboard(tenant.rbacApiKey, 'RBAC API Key')}
                    data-testid="button-copy-rbac"
                  >
                    <Copy className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Integration Example */}
          <Card>
            <CardHeader>
              <CardTitle className="text-lg">Integration Example</CardTitle>
            </CardHeader>
            <CardContent>
              <pre className="bg-slate-900 text-slate-300 p-4 rounded-lg text-sm overflow-x-auto">
                <code>{`import { SaaSAuth, SaaSRBAC } from '@saas-framework/auth';

const auth = new SaaSAuth({
  apiKey: '${showApiKeys ? tenant.authApiKey : 'YOUR_AUTH_API_KEY'}',
  baseUrl: 'https://api.yourplatform.com/api/v2/auth'
});

const rbac = new SaaSRBAC({
  apiKey: '${showApiKeys ? tenant.rbacApiKey : 'YOUR_RBAC_API_KEY'}',
  baseUrl: 'https://api.yourplatform.com/api/v2/rbac'
});`}</code>
              </pre>
            </CardContent>
          </Card>
        </div>
      </DialogContent>
    </Dialog>
  );
}

