export default function RBACManagementPage() {
  const { tenantId } = useParams();
  const [activeSection, setActiveSection] = useState('configuration');
  const [isRoleDialogOpen, setIsRoleDialogOpen] = useState(false);
  const [isPermissionDialogOpen, setIsPermissionDialogOpen] = useState(false);
  const [editingRole, setEditingRole] = useState<any>(null);
  const [editingPermission, setEditingPermission] = useState<any>(null);
  const queryClient = useQueryClient();
  const { toast } = useToast();

  // Fetch tenant data
  const { data: tenant } = useQuery({
    queryKey: ["/api/tenants", tenantId],
    enabled: !!tenantId,
  });

  const { data: roles = [] } = useQuery({
    queryKey: ["/api/tenants", tenantId, "roles"],
    enabled: !!tenantId,
  });

  const { data: permissions = [] } = useQuery({
    queryKey: ["/api/tenants", tenantId, "permissions"],
    enabled: !!tenantId,
  });

  // Forms
  const roleForm = useForm<RoleForm>({
    resolver: zodResolver(roleSchema),
    defaultValues: { name: "", description: "", permissions: [] },
  });

  const permissionForm = useForm<PermissionForm>({
    resolver: zodResolver(permissionSchema),
    defaultValues: { key: "", description: "", category: "" },
  });

  const configForm = useForm<RBACConfigForm>({
    resolver: zodResolver(rbacConfigSchema),
    defaultValues: {
      permissionTemplate: "standard",
      businessType: "general",
    },
  });

  // Mutations
  const createRoleMutation = useMutation({
    mutationFn: async (data: RoleForm) => {
      const response = await fetch(`/api/tenants/${tenantId}/roles`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(data),
      });
      if (!response.ok) throw new Error('Failed to create role');
      return response.json();
    },
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ["/api/tenants", tenantId, "roles"] });
      toast({ title: "Role created successfully" });
      setIsRoleDialogOpen(false);
      roleForm.reset();
    },
  });

  const businessTypeInfo = {
    general: { icon: Building2, label: "General Business", description: "Standard business operations" },
    healthcare: { icon: FileText, label: "Healthcare", description: "Healthcare providers and medical facilities" },
    finance: { icon: CreditCard, label: "Finance", description: "Financial institutions and services" },
    education: { icon: GraduationCap, label: "Education", description: "Schools and educational institutions" },
    government: { icon: Landmark, label: "Government", description: "Government agencies and public sector" },
  };

  const renderConfiguration = () => (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Settings className="h-5 w-5" />
          RBAC Configuration
        </CardTitle>
        <CardDescription>
          Configure permission templates and business type settings for this tenant
        </CardDescription>
      </CardHeader>
      <CardContent className="space-y-6">
        <div>
          <h3 className="text-lg font-semibold mb-4">Permission Template</h3>
          <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
            {[
              {
                value: "standard",
                label: "Standard",
                description: "Basic set of permissions for general use cases. Includes core user management, role assignment, and essential business operations.",
                permissions: "8-12 permissions",
                recommended: true,
              },
              {
                value: "enterprise",
                label: "Enterprise",
                description: "Advanced permissions with audit trails, compliance features, and system administration capabilities.",
                permissions: "15+ permissions",
                recommended: false,
              },
              {
                value: "custom",
                label: "Custom",
                description: "Build your own permission set from scratch based on your specific requirements.",
                permissions: "Unlimited",
                recommended: false,
              },
            ].map((template) => (
              <Card
                key={template.value}
                className="relative cursor-pointer hover:shadow-md transition-shadow"
              >
                <CardContent className="p-4">
                  {template.recommended && (
                    <Badge className="absolute -top-2 -right-2 bg-blue-500">
                      Recommended
                    </Badge>
                  )}
                  <div className="flex items-center gap-2 mb-2">
                    <Shield className="h-5 w-5 text-blue-500" />
                    <h4 className="font-semibold">{template.label}</h4>
                  </div>
                  <p className="text-sm text-slate-600 mb-2">{template.description}</p>
                  <p className="text-xs text-blue-600 font-medium">{template.permissions}</p>
                </CardContent>
              </Card>
            ))}
          </div>
        </div>

        <Separator />

        <div>
          <h3 className="text-lg font-semibold mb-4">Business Type</h3>
          <p className="text-sm text-slate-600 mb-4">
            Choose your business type to get industry-specific role templates and permission sets that match your operational needs.
          </p>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
            {Object.entries(businessTypeInfo).map(([type, info]) => {
              const Icon = info.icon;
              return (
                <Card
                  key={type}
                  className="cursor-pointer hover:shadow-md transition-shadow"
                >
                  <CardContent className="p-4">
                    <div className="flex items-center gap-3 mb-2">
                      <Icon className="h-5 w-5 text-blue-500" />
                      <h4 className="font-semibold">{info.label}</h4>
                    </div>
                    <p className="text-sm text-slate-600">{info.description}</p>
                  </CardContent>
                </Card>
              );
            })}
          </div>
        </div>

        <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
          <div className="flex items-start gap-3">
            <CheckCircle className="h-5 w-5 text-blue-600 mt-0.5" />
            <div>
              <h4 className="font-semibold text-blue-900">Current Configuration</h4>
              <p className="text-sm text-blue-700 mt-1">
                <strong>Permission Template:</strong> Standard - Provides essential user management and role-based permissions suitable for most applications.
              </p>
              <p className="text-sm text-blue-700 mt-1">
                <strong>Business Type:</strong> General - Standard business operations with Admin, Manager, and User roles.
              </p>
            </div>
          </div>
        </div>

        <div className="flex justify-end">
          <Button>Apply Configuration Changes</Button>
        </div>
      </CardContent>
    </Card>
  );

  const renderRoles = () => (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-semibold">Roles Management</h2>
          <p className="text-sm text-slate-600">Create and manage user roles with specific permissions</p>
        </div>
        <Dialog open={isRoleDialogOpen} onOpenChange={setIsRoleDialogOpen}>
          <DialogTrigger asChild>
            <Button>
              <Plus className="h-4 w-4 mr-2" />
              Add Role
            </Button>
          </DialogTrigger>
          <DialogContent className="sm:max-w-2xl">
            <DialogHeader>
              <DialogTitle>Create New Role</DialogTitle>
              <DialogDescription>
                Define a new role with specific permissions for your users
              </DialogDescription>
            </DialogHeader>
            <Form {...roleForm}>
              <form className="space-y-4">
                <FormField
                  control={roleForm.control}
                  name="name"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Role Name</FormLabel>
                      <FormControl>
                        <Input placeholder="e.g. Admin, Manager, User" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <FormField
                  control={roleForm.control}
                  name="description"
                  render={({ field }) => (
                    <FormItem>
                      <FormLabel>Description</FormLabel>
                      <FormControl>
                        <Textarea placeholder="Describe the role's purpose and scope" {...field} />
                      </FormControl>
                      <FormMessage />
                    </FormItem>
                  )}
                />
                <DialogFooter>
                  <Button type="button" variant="outline" onClick={() => setIsRoleDialogOpen(false)}>
                    Cancel
                  </Button>
                  <Button type="button">Create Role</Button>
                </DialogFooter>
              </form>
            </Form>
          </DialogContent>
        </Dialog>
      </div>

      {/* Default Roles Display */}
      <div className="grid gap-4">
        {DEFAULT_ROLES.general.standard.map((role, index) => (
          <Card key={index}>
            <CardContent className="p-4">
              <div className="flex justify-between items-start">
                <div className="flex-1">
                  <div className="flex items-center gap-2 mb-2">
                    <Users className="h-5 w-5 text-blue-500" />
                    <h3 className="font-semibold">{role.name}</h3>
                    <Badge variant="outline">Default</Badge>
                  </div>
                  <p className="text-sm text-slate-600 mb-3">{role.description}</p>
                  <div className="flex flex-wrap gap-2">
                    {role.permissions.map((permission, idx) => (
                      <Badge key={idx} variant="outline" className="text-xs">
                        {permission}
                      </Badge>
                    ))}
                  </div>
                </div>
                <div className="flex items-center gap-2 ml-4">
                  <Button variant="outline" size="sm">
                    <Edit className="h-4 w-4" />
                  </Button>
                  <Button variant="outline" size="sm">
                    <Copy className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderPermissions = () => (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h2 className="text-xl font-semibold">Permissions Management</h2>
          <p className="text-sm text-slate-600">Define granular permissions for your application</p>
        </div>
        <Button>
          <Plus className="h-4 w-4 mr-2" />
          Add Permission
        </Button>
      </div>

      {/* Permission Categories */}
      <div className="space-y-4">
        {Object.entries({
          "User Management": PERMISSION_TEMPLATES.general.standard.filter(p => p.category === "User Management"),
          "Role Management": PERMISSION_TEMPLATES.general.standard.filter(p => p.category === "Role Management"),
        }).map(([category, perms]) => (
          <Card key={category}>
            <CardHeader className="pb-4">
              <CardTitle className="text-lg flex items-center gap-2">
                <Key className="h-5 w-5" />
                {category}
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="grid gap-3">
                {perms.map((permission, index) => (
                  <div key={index} className="flex items-center justify-between p-3 border rounded-lg">
                    <div>
                      <div className="font-medium text-sm">{permission.key}</div>
                      <div className="text-xs text-slate-600">{permission.description}</div>
                    </div>
                    <div className="flex items-center gap-2">
                      <Button variant="outline" size="sm">
                        <Edit className="h-4 w-4" />
                      </Button>
                      <Button variant="outline" size="sm" className="text-red-600 hover:text-red-700">
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>
    </div>
  );

  const renderAssignments = () => (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Users className="h-5 w-5" />
          User Role Assignments
        </CardTitle>
        <CardDescription>
          Assign roles to users and manage their permissions
        </CardDescription>
      </CardHeader>
      <CardContent className="p-8 text-center">
        <Users className="h-12 w-12 mx-auto mb-4 text-slate-400" />
        <h3 className="font-semibold mb-2">No users assigned</h3>
        <p className="text-sm text-slate-600 mb-4">
          User assignment functionality will be available once you have users in your tenant
        </p>
        <Button variant="outline">Import Users</Button>
      </CardContent>
    </Card>
  );

  return (
    <div className="min-h-screen bg-slate-50 flex">
      {/* Sidenav */}
      <div className="w-64 bg-white border-r border-slate-200 flex-shrink-0">
        <div className="p-6">
          <div className="flex items-center gap-2 mb-6">
            <Shield className="h-6 w-6 text-blue-500" />
            <span className="font-semibold text-slate-800">RBAC Manager</span>
          </div>

          <nav className="space-y-2">
            <button
              onClick={() => setActiveSection('configuration')}
              className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors ${
                activeSection === 'configuration' ? 'bg-blue-50 text-blue-700 font-medium' : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Settings className="h-4 w-4" />
              Configuration
            </button>
            <button
              onClick={() => setActiveSection('roles')}
              className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors ${
                activeSection === 'roles' ? 'bg-blue-50 text-blue-700 font-medium' : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Users className="h-4 w-4" />
              Roles
            </button>
            <button
              onClick={() => setActiveSection('permissions')}
              className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors ${
                activeSection === 'permissions' ? 'bg-blue-50 text-blue-700 font-medium' : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Key className="h-4 w-4" />
              Permissions
            </button>
            <button
              onClick={() => setActiveSection('assignments')}
              className={`w-full flex items-center gap-3 px-3 py-2 rounded-lg text-sm transition-colors ${
                activeSection === 'assignments' ? 'bg-blue-50 text-blue-700 font-medium' : 'text-slate-600 hover:bg-slate-50'
              }`}
            >
              <Users className="h-4 w-4" />
              User Assignments
            </button>
          </nav>
        </div>
      </div>

      {/* Main Content */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <div className="bg-white border-b border-slate-200">
          <div className="px-6 py-4">
            <div className="flex items-center justify-between">
              <div>
                <h1 className="text-2xl font-bold text-slate-800">RBAC Management</h1>
                <p className="text-sm text-slate-600">Roles, permissions and access control for {(tenant as any)?.name || 'this tenant'}</p>
              </div>
              <Link href={`/tenants/${tenantId}/portal`}>
                <Button variant="outline">â† Back to Portal</Button>
              </Link>
            </div>
          </div>
        </div>

        <div className="flex-1 p-6">
          {activeSection === 'configuration' && renderConfiguration()}
          {activeSection === 'roles' && renderRoles()}
          {activeSection === 'permissions' && renderPermissions()}
          {activeSection === 'assignments' && renderAssignments()}
        </div>
      </div>
    </div>
  );
}

server/services/compliance.ts.content:
import { db } from '../db';
import { complianceAuditLogs, securityEvents } from '../../shared/schema';
import type { InsertComplianceAuditLog, InsertSecurityEvent } from '../../shared/schema';

export class ComplianceService {

  /**
   * Log RBAC-related compliance events
   */
  async logRBACEvent(data: {
    tenantId: string;
    userId?: string;
    adminUserId?: string;
    action: string; // role_assigned, role_removed, permission_granted, permission_revoked
    entityType: 'user' | 'role' | 'permission';
    entityId: string;
    entityName?: string;
    beforeState?: any;
    afterState?: any;
    sessionId?: string;
    ipAddress?: string;
    userAgent?: string;
    riskLevel?: 'low' | 'medium' | 'high' | 'critical';
  }) {
    const auditLog: InsertComplianceAuditLog = {
      tenantId: data.tenantId,
      userId: data.userId || null,
      adminUserId: data.adminUserId || null,
      eventType: 'rbac_change',
      eventCategory: this.getEventCategory(data.action),
      entityType: data.entityType,
      entityId: data.entityId,
      entityName: data.entityName || null,
      action: data.action,
      outcome: 'success',
      riskLevel: data.riskLevel || 'medium', // RBAC changes are typically medium risk
      complianceFrameworks: ['sox', 'iso27001'], // RBAC is relevant for these frameworks
      dataClassification: 'internal',
      details: {
        action: data.action,
        entityType: data.entityType,
        changes: data.afterState ? Object.keys(data.afterState) : []
      },
      beforeState: data.beforeState || null,
      afterState: data.afterState || null,
      sessionId: data.sessionId || null,
      ipAddress: data.ipAddress || null,
      userAgent: data.userAgent || null,
      retentionUntil: this.calculateRetentionDate('rbac', 7) // 7 years for SOX compliance
    };

    await db.insert(complianceAuditLogs).values(auditLog);
  }

  /**
   * Log authentication events
   */
  async logAuthEvent(data: {
    tenantId: string;
    userId?: string;
    action: string; // login_success, login_failed, logout, password_reset, mfa_enabled
    outcome: 'success' | 'failure' | 'blocked';
    sessionId?: string;
    ipAddress?: string;
    userAgent?: string;
    geolocation?: any;
    riskLevel?: 'low' | 'medium' | 'high' | 'critical';
  }) {
    const auditLog: InsertComplianceAuditLog = {
      tenantId: data.tenantId,
      userId: data.userId || null,
      adminUserId: null,
      eventType: 'auth_event',
      eventCategory: this.getEventCategory(data.action),
      entityType: 'session',
      entityId: data.sessionId || 'unknown',
      entityName: null,
      action: data.action,
      outcome: data.outcome,
      riskLevel: data.riskLevel || (data.outcome === 'failure' ? 'medium' : 'low'),
      complianceFrameworks: ['gdpr', 'sox'],
      dataClassification: 'confidential',
      details: {
        action: data.action,
        outcome: data.outcome,
        ipAddress: data.ipAddress,
        userAgent: data.userAgent
      },
      sessionId: data.sessionId || null,
      ipAddress: data.ipAddress || null,
      userAgent: data.userAgent || null,
      geolocation: data.geolocation || null,
      retentionUntil: this.calculateRetentionDate('auth', 3) // 3 years for auth logs
    };

    await db.insert(complianceAuditLogs).values(auditLog);

    // Also create security event if it's a failed login
    if (data.outcome === 'failure' && data.action.includes('login')) {
      await this.logSecurityEvent({
        tenantId: data.tenantId,
        eventType: 'authentication_failure',
        severity: 'warning',
        source: 'api',
        userId: data.userId,
        ipAddress: data.ipAddress,
        userAgent: data.userAgent,
        details: {
          action: data.action,
          sessionId: data.sessionId
        }
      });
    }
  }

  /**
   * Log data access events for GDPR compliance
   */
  async logDataAccessEvent(data: {
    tenantId: string;
    userId?: string;
    adminUserId?: string;
    action: string; // data_exported, data_deleted, data_updated, pii_accessed
    entityType: string;
    entityId: string;
    entityName?: string;
    dataClassification: 'public' | 'internal' | 'confidential' | 'restricted';
    sessionId?: string;
    ipAddress?: string;
    userAgent?: string;
  }) {
    const auditLog: InsertComplianceAuditLog = {
      tenantId: data.tenantId,
      userId: data.userId || null,
      adminUserId: data.adminUserId || null,
      eventType: 'data_access',
      eventCategory: this.getEventCategory(data.action),
      entityType: data.entityType,
      entityId: data.entityId,
      entityName: data.entityName || null,
      action: data.action,
      outcome: 'success',
      riskLevel: this.getRiskLevelForDataAccess(data.action, data.dataClassification),
      complianceFrameworks: this.getComplianceFrameworks(data.dataClassification),
      dataClassification: data.dataClassification,
      details: {
        action: data.action,
        entityType: data.entityType,
        dataClassification: data.dataClassification
      },
      sessionId: data.sessionId || null,
      ipAddress: data.ipAddress || null,
      userAgent: data.userAgent || null,
      retentionUntil: this.calculateRetentionDate('data_access', 6) // 6 years for GDPR
    };

    await db.insert(complianceAuditLogs).values(auditLog);
  }

  /**
   * Log security events
   */
  async logSecurityEvent(data: InsertSecurityEvent) {
    await db.insert(securityEvents).values(data);
  }

  /**
   * Get compliance reports for auditors
   */
  async getComplianceReport(options: {
    tenantId?: string;
    framework?: string; // gdpr, sox, hipaa, pci, iso27001
    startDate: Date;
    endDate: Date;
    eventTypes?: string[];
    riskLevels?: string[];
    limit?: number;
  }) {
    // Implementation would return formatted compliance data
    // This is a placeholder for the actual reporting logic
    return {
      summary: {
        totalEvents: 0,
        highRiskEvents: 0,
        complianceViolations: 0,
        dataAccessEvents: 0,
        rbacChanges: 0
      },
      events: [],
      recommendations: []
    };
  }

  // Helper methods
  private getEventCategory(action: string): string {
    if (action.includes('create') || action.includes('assign')) return 'create';
    if (action.includes('read') || action.includes('access') || action.includes('view')) return 'read';
    if (action.includes('update') || action.includes('modify')) return 'update';
    if (action.includes('delete') || action.includes('remove')) return 'delete';
    if (action.includes('login')) return 'access';
    if (action.includes('logout')) return 'access';
    return 'access';
  }

  private getRiskLevelForDataAccess(action: string, classification: string): 'low' | 'medium' | 'high' | 'critical' {
    if (classification === 'restricted') return 'critical';
    if (classification === 'confidential') return 'high';
    if (action.includes('export') || action.includes('delete')) return 'high';
    if (classification === 'internal') return 'medium';
    return 'low';
  }

  private getComplianceFrameworks(dataClassification: string): string[] {
    const frameworks = ['gdpr']; // GDPR applies to all personal data

    if (dataClassification === 'restricted' || dataClassification === 'confidential') {
      frameworks.push('sox', 'iso27001');
    }

    return frameworks;
  }

  private calculateRetentionDate(eventType: string, years: number): Date {
    const retentionDate = new Date();
    retentionDate.setFullYear(retentionDate.getFullYear() + years);
    return retentionDate;
  }
}

export const complianceService = new ComplianceService();

